// A simple Class (Blueprint)
public class Car {
    // State (Fields)
    private String color;
    private int speed;

    // Behavior (Methods)
    public void accelerate() {
        speed += 5;
    }
    public void displaySpeed() {
        System.out.println("Speed: " + speed + " km/h");
    }
}

// INHERITANCE EXAMPLE
// Superclass (Parent)
class Vehicle {
    void run() {
        System.out.println("Vehicle is running");
    }
}
// Subclass (Child)
class Bike extends Vehicle { // 'extends' keyword for inheritance
    @Override
    void run() { // Method Overriding (POLYMORPHISM)
        System.out.println("Bike is running safely");
    }
}

// Procedural-style code in Java (often uses static methods)
public class MathOperations {
    // A static method (can be called without creating an object)
    public static int add(int a, int b) {
        return a + b;
    }

    public static void main(String[] args) { // Procedural sequence
        int x = 10;
        int y = 20;
        int sum = add(x, y); // Procedure call
        System.out.println("Sum: " + sum);
    }
}

public class HelloWorld { 
    public static void main(String[] args) { 
        System.out.println("Hello, World!"); 
    }
}

// Line-by-line breakdown
public class HelloWorld { // Class declaration block starts

    // Main method declaration block starts
    public static void main(String[] args) {
        // Statement: Print to console
        System.out.println("Hello, World!");
    } // Main method block ends

} // Class block ends

public class SecondProgram {
    public static void main(String[] args) {
        int num; // this declares a variable called 'num' of type int (integer)
        num = 100; // this assigns the value 100 to the variable 'num'

        System.out.println("This is num: " + num); // Concatenates text and variable

        num = num * 2; // Calculates num * 2 and assigns the result back to num
        System.out.print("The value of num * 2 is "); // 'print' doesn't add a newline
        System.out.println(num);
    }
}
